/**
 * Module Loader - Load scripts only when needed
 * 
 * @author Marvin Arlt
 * @date 2020-04-10
 * @version 0.1
 */
function moduleLoader(e){for(let t in e)if(null!==document.querySelector(t)){const n=e[t].scripts;let o=e[t].function;if(n.length>0)for(let e=0;e<n.length;e++){const t=location.origin+n[e],o=document.createElement("script");o.defer=!0,o.src=t,document.body.appendChild(o)}"string"==typeof o&&(o=window[o]),"function"==typeof o&&o()}}